methods.examples:
// add a permission for the special authenticated group to see the workspace of the job
job('example-1') {
    authorization {
        permission('hudson.model.Item.Workspace:authenticated')
    }
}

// adds the build permission for the special anonymous user
job('example-2') {
    authorization {
        permission('hudson.model.Item.Build', 'anonymous')
    }
}

// add all permissions for user joe
job('example') {
    authorization {
        permissionAll('joe')
    }
}

job('example') {
    environmentVariables {
        groovy('''
            def a = 1
            return [TWO: 2 * a]
        ''')
    }
}

job('example') {
    environmentVariables {
        script('''
           mkdir -p tests
           rm -rf /tmp/tests.tmp
        ''')
    }
}

// defining config spec and load rules
job('example-1') {
    scm {
        baseClearCase {
            configSpec('''element * CHECKEDOUT
element * /main/LATEST''')
            loadRules('/vob/some_vob')
        }
    }
}

// defining config spec and load rules with multiple methods calls
job('example-1') {
    scm {
        baseClearCase {
            configSpec('element * CHECKEDOUT')
            configSpec('element * /main/LATEST')
            loadRules('/vob/some_vob')
            loadRules('/vob/another_vob')
        }
    }
}

// defining config spec and load rules using varargs parameters
job('example-1') {
    scm {
        baseClearCase {
            configSpec('element * CHECKEDOUT', 'element * /main/LATEST')
            loadRules('/vob/some_vob', '/vob/another_vob')
        }
    }
}

// reading the config spec from a file in the seed job's workspace
job('example-1') {
    scm {
        baseClearCase {
            configSpec(readFileFromWorkspace('configSpec.txt'))
            loadRules('/vob/some_vob')
        }
    }
}

// checkout repo1 to a sub directory and clean the workspace after checkout
job('example-1') {
    scm {
        git {
            remote {
                name('remoteB')
                url('git@server:account/repo1.git')
            }
            extensions {
                cleanAfterCheckout()
                relativeTargetDirectory('repo1')
            }
        }
    }
}

// add the upstream repository as second remote and
// merge branch featureA with master
job('example-2') {
    scm {
        git {
            remote {
                name('origin')
                url('git@serverA:account/repo1.git')
            }
            remote {
                name('upstream')
                url('git@serverB:account/repo1.git')
            }
            branch('featureA')
            extensions {
                mergeOptions {
                    remote('upstream')
                    branch('master')
                }
            }
        }
    }
}

// add user name and email options
job('example-3') {
    scm {
        git('git@git') { node -> // is hudson.plugins.git.GitSCM
            node / gitConfigName('DSL User')
            node / gitConfigEmail('me@me.com')
        }
    }
}

// add Git SCM for a GitHub repository with authentication
job('example-4') {
    scm {
        git {
            remote {
                github('account/repo', 'ssh')
                credentials('github-ci-key')
            }
        }
    }
}

// checkout at a specific branch using the alternative build choosing strategy
job('example-5') {
    scm {
        git {
            remote {
                github('account/repo', 'ssh')
            }
            branches('branch-that-may-not-exist', 'master')
            extensions {
                choosingStrategy {
                    alternative()
                }
            }
        }
    }
}

// checkout feature_branch1
job('example-1') {
    scm {
        hg('http://scm', 'feature_branch1')
    }
}

// clean checkout module1 from feature_branch1
job('example-2') {
    scm {
        hg('http://scm') {
            branch('feature_branch1')
            modules('module1')
            clean(true)
        }
    }
}

job('example') {
    scm {
        p4('//depot/Tools/build', 'rolem') { node ->
            node / p4Port('perforce:1666')
            node / p4Tool('/usr/bin/p4')
            node / exposeP4Passwd('false')
            node / pollOnlyOnMaster('true')
        }
    }
}

job('example') {
    scm {
        perforceP4('p4_credentials') {
            workspace {
                manual('ws_name', '//depot/Tools/build/... //ws_name/build/...')
            }
            configure { node ->
                node / workspace / spec / clobber('true')
            }
        }
    }
}

job('example-1') {
    scm {
        rtc {
            buildDefinition('buildDefinitionInRTC')
        }
    }
}

job('example-2') {
    scm {
        rtc {
            buildWorkspace('some-workspace')
            connection('my-build-tool', 'build-user', 'https://localhost:9444/ccm', 60)
        }
